import React from 'react';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import RegisterForm from './components/RegisterForm';
import LocationFilter from './components/LocationFilter';

function App() {
  return (
    <div>
      <ToastContainer position="top-right" autoClose={3000} />
      <div className="home-page">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-container">
          <h1 className="project-title">My Project Title</h1>
          <div className="navbar-links">
            <a href="#register">Register</a>
            <a href="#location">Location Filter</a>
          </div>
        </div>
      </nav>

      {/* Main Section */}
      <div className="main-section">
        <div id="register" className="section-card">
          <RegisterForm />
        </div>
        <div id="location" className="section-card">
          <LocationFilter />
        </div>
      </div>

      {/* Post Cards Section */}
      <div className="posts-section">
        <h2 className="posts-title">Latest Posts</h2>
        <div className="posts-grid">
          {[1, 2, 3, 4, 5].map((post) => (
            <div key={post} className="post-card">
              <h3 className="post-title">Post Title {post}</h3>
              <p className="post-description">This is a sample description for post {post}.</p>
            </div>
          ))}
        </div>
      </div>
    </div>
    </div>
  );
}

export default App;
