import React from 'react';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import RegisterForm from './components/RegisterForm';
import LocationFilter from './components/LocationFilter';
import './HomePage.css';

function App() {
  return (
    <div>
      <ToastContainer position="top-right" autoClose={3000} />
      <div className="home-page">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-content">
          <div>
            <h1 className="project-title">My Project Title</h1>
            <p className="project-description">This is a brief description of the project.</p>
          </div>
          <div className="nav-links">
            <a href="#register">Register</a>
            <a href="#location">Location Filter</a>
          </div>
        </div>
      </nav>

      {/* Sections */}
      <div className="sections">
        <section id="register">
          <RegisterForm />
        </section>
        <section id="location">
          <LocationFilter />
        </section>
      </div>

      {/* Grid Cards */}
      <div className="cards-grid">
        {[1, 2, 3, 4, 5].map((card) => (
          <div key={card} className="card">
            <h3>Post Title {card}</h3>
            <p>This is a sample description for post {card}.</p>
          </div>
        ))}
      </div>
    </div>
  );
    </div>
  );
}

export default App;
