import React, { useState } from 'react';
import axios from 'axios';
import MyMap from './MyMap'; // ‚úÖ Import MyMap

function LocationFilter() {
  const [phone, setPhone] = useState('');
  const [location, setLocation] = useState(null);
  const [address, setAddress] = useState('');
  const [loading, setLoading] = useState(false);

  const handlePhoneChange = (e) => {
    const value = e.target.value.replace(/\D/g, '').slice(0, 10);
    setPhone(value);
  };

  const handleSearch = async () => {
    if (!/^[0-9]{10}$/.test(phone)) {
      alert('‚ùå Please enter a valid 10-digit phone number');
      return;
    }

    setLoading(true);
    setAddress('');

    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/users/location/${phone}`);
      console.log('‚úÖ Response:', res.data);

      if (res.data) {
        const { latitude, longitude, address } = res.data;

        if (!isNaN(latitude) && !isNaN(longitude)) {
          setLocation({ lat: latitude, lng: longitude });
          setAddress(address || '‚ùå Address not found.');
        } else {
          setAddress('‚ùå Invalid location data received.');
        }
      }
    } catch (err) {
      console.error('‚ùå Location fetch failed:', err);
      setAddress(err.response?.data?.message || '‚ùå Failed to fetch location. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h2>üîé Find Location</h2>
      <input
        type="text"
        placeholder="Enter Phone Number"
        value={phone}
        onChange={handlePhoneChange}
        style={{
          padding: '10px',
          width: '80%',
          maxWidth: '300px',
          borderRadius: '5px',
          border: '1px solid #ccc',
          marginBottom: '10px',
          fontSize: '16px',
          outline: 'none',
        }}
        disabled={loading}
      />
      <button
        onClick={handleSearch}
        style={{
          padding: '10px 20px',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          marginLeft: '10px',
          fontSize: '16px',
          transition: 'background 0.3s',
          opacity: loading ? 0.7 : 1,
        }}
        disabled={loading}
      >
        {loading ? 'Searching...' : 'Search'}
      </button>

      {/* Use MyMap instead of duplicating MapContainer */}
      {location && <MyMap lat={location.lat} lng={location.lng} />}

      {address && <p style={{ marginTop: '10px', color: '#555' }}>{address}</p>}
    </div>
  );
}

export default LocationFilter;
